def publish_article_to_stopdelay_blog(
        html_title: str,
        url_slug: str,
        meta_description: str,
        article_content: str,
        article_picture: str,
        category_id: int,
        tags_list: list,
        api_url: str,
        auth_token: str,
        language: str
) -> requests.Response:
    """
    Sends an article with its associated metadata to an API endpoint via POST request.

    Args:
        html_title (str): The title of the article to be used in HTML
        url_slug (str): The URL-friendly slug for the article
        meta_description (str): Meta description for SEO purposes
        article_content (str): The main content of the article
        article_picture (str): File path to the JPEG image for the article
        api_url (str): The target API endpoint URL
        auth_token (str): Authorization token for API authentication

    Returns:
        requests.Response: The response from the API

    Raises:
        FileNotFoundError: If the article_picture file doesn't exist
        ValueError: If the article_picture is not a JPEG file
    """


    # Prepare the headers with authorization
    headers = {
        'App-AUTH': f'SJWT {auth_token}',
        'Content-Type': 'application/json'
    }

    # Prepare the request payload
    payload = {
        "blog":{
            'title': html_title,
            # 'meta_title': html_title,
            'slug': url_slug,
            # 'meta_description': meta_description,
            'short_description': meta_description,
            # 'article_html_content': article_content,
            'feature_image': article_picture,
            'category_id': category_id,
            'tags': tags_list,
        },
        "translations": [
            {
                "language": language,
                'title': html_title,
                "slug": url_slug,
                "short_description": meta_description,
                'article_html_content': article_content,
                'meta_title': html_title,
                'meta_description': meta_description
            }
        ],
    }

    try:
        # Send the POST request
        response = requests.post(
            url=api_url,
            headers=headers,
            json=payload  # requests will automatically JSON-encode this dictionary
        )

        # Raise an exception for bad status codes
        response.raise_for_status()

        return response.json()

    except requests.exceptions.RequestException as e:
        # Log the error (in a real application, you'd want proper logging)
        print(f"Error sending request of uploading an article to the stopdelay blog: {str(e)}")
        raise  # Re-raise the exception for the caller to handle


